function transform (message, context) {
  const roster = message.payload.roster;

  const grouped = {};

  roster.forEach(player => {
    const key = player.player_name;

    if (!grouped[key]) {
      const { competition, ...rest } = player;
      grouped[key] = {
        ...rest,
        competitions: [competition]
      };
    } else {
      grouped[key].competitions.push(player.competition);
    }
  });

  const groupedRoster = Object.values(grouped);

  message.payload.roster = groupedRoster;

  return message;
}

function transform (message, context) {

  const { player_name, 
    birth_date,
    phone_number,
    email_address,
    parent_name,
    team_name,
    yr,
    organization,
    coach_name,
    coach_phone_number,
    competitions
  } = message.payload

  message.header.fileName = `${player_name} - Invitation.html`

  message.header.player = {
    name: player_name,
    birth_date,
    phone_number,
    email_address,
    parent_name,
    competitions
  }

  message.header.coach = {
    name: coach_name,
    phone_number: coach_phone_number,
    email_address: context.get('coachEmail')
  };

  message.header.general = {
    team_name,
    year: yr,
    organization
  };

  return message;
}

